mapper.select:
select * from  user_role_info


       and create_time >= Date(#{datefrom}) 


       and create_time <= Date(#{dateto}) 


      and isorg = #{isorg}



      and isadmin= #{isadmin}



      and isexperter= #{isexperter}



      and ismanager= #{ismanager}



      and isengineer= #{isengineer}



      and isent= #{isent}



      and isgov= #{isgov}


order by uid desc limit #{showPages},#{pageSize}

select count(*) from user_role_info 


       and create_time >= Date(#{datefrom}) 


       and create_time <= Date(#{dateto}) 


      and isorg = #{isorg}



      and isadmin= #{isadmin}



      and isexperter= #{isexperter}



      and ismanager= #{ismanager}



      and isengineer= #{isengineer}



      and isent= #{isent}



      and isgov= #{isgov}
