mapper.select:
select * from user_login_info 


       username = #{username}


       and create_time >= Date(#{datefrom}) 


       and create_time <= Date(#{dateto}) 


      and email = #{email}



      and uwechatid = #{uwechatid}



      and status = #{status}



order by uid desc limit #{showPages},#{pageSize}

select count(*) from user_login_info


       username = #{username}


       and create_time >= Date(#{datefrom}) 


       and create_time <= Date(#{dateto}) 


      and email = #{email}



      and uwechatid = #{uwechatid}



      and status = #{status}
